	/**
	 * Retrieve a single object by pkey.
	 *
	 * @param      int $pk the primary key.
	 * @param      PropelPDO $con the connection to use
	 * @return     Games
	 */
	public static function retrieveByPK($pk, PropelPDO $con = null)
	{

		if (null !== ($obj = GamesPeer::getInstanceFromPool((string) $pk))) {
			return $obj;
		}

		if ($con === null) {
			$con = Propel::getConnection(GamesPeer::DATABASE_NAME, Propel::CONNECTION_READ);
		}

		$criteria = new Criteria(GamesPeer::DATABASE_NAME);
		$criteria->add(GamesPeer::ID, $pk);

		$v = GamesPeer::doSelect($criteria, $con);

		return !empty($v) > 0 ? $v[0] : null;
	}

